INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1365, 'YYYY', 'number', '', 'Ano a ser usado como critério da pesquisa');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1366, 'MM', 'number', '', 'Mês a ser usado como critério da pesquisa - Válido somente quando a pesquisa for de natureza conjuntural, ou seja, cuja periodicidade de publicação seja inferior a um ano. Informar conforme a ordem do mês no ano - Janeiro = 1, Fevereiro = 2 etc');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1367, 'order', 'number', '', 'Caso haja mais de uma publicação de uma mesma pesquisa para um mesmo período pesquisado, a ordem de publicação será definida pelo parâmetro order, sendo o numeral 0 uma referência à primeira ocorrência no período. Caso omitido, assume o valor 0');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1368, 'nome', 'string', '', 'Um ou mais nomes delimitados pelo caractere | (pipe)');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1369, 'sexo', 'string', '', 'Por padrão, a consulta pelo nome é unissex. Caso deseje filtrar pelo sexo, informe o parâmetro sexo, cujos valores podem ser M, para o sexo masculino, ou F, para o feminino');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1370, 'groupBy', 'string', '', 'Caso deseje obter a frequência por algum nível geográfico, informe o parâmetro groupBy - Válido apenas quando informado um único nome. Nesta versão, apenas o valor UF é válido, no qual se obtém a frequência do nome informado por Unidade da Federação');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1371, 'localidade', 'string', '', 'Caso deseje obter a frequência referente a uma dada localidade, informe o parâmetro localidade. ');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1372, 'decada', 'string', '', 'Caso deseje obter o ranking referente a uma década de nascimento específica, informe o parâmetro década');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1373, 'tipo', 'string', '', 'Informa se deseja consultar Notícias ou Releases. Não informando, retornará ambos os tipos.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1374, 'qtd', 'integer', '', 'Quantidade de itens por página. Caso não informado, será retornado todos os itens encontrados.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1375, 'page', 'integer', '', 'Página desejada da listagem, levando em conta a quantidade de itens por página. Caso não informado, será retornada a primeira página.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1376, 'de', 'string', 'MM-DD-YYYY', 'Data mínima de publicação. Formato MM-DD-YYYY');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1377, 'ate', 'string', 'MM-DD-YYYY', 'Data máxima de publicação. MM-DD-YYYY');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1378, 'introsize', 'integer', '', 'Tamanho máximo do texto de introdução. O texto será truncado no valor estipulado.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1379, 'destaque', 'integer', '', 'Informa se o resultado deverá vir ordenado por destaque ou não. O padrão é sim.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1380, 'busca', 'string', '', 'Termo de busca para consulta de notícias e releases.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1381, 'idproduto', 'string', '', 'Um ou mais identificadores de pesquisas ou produtos delimitados pelo caracter | (pipe) ');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1382, 'indicador ', 'string', '', 'Um ou mais identificadores de indicadores delimitados pelo caractere pipe (|).');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1383, 'paises', 'string', '', 'Um ou mais identificadores de países delimitados pelo caractere pipe (|) ou informe - para obter os países sem filtro.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1384, 'periodo', 'string', '', 'Se desejar filtrar os indicadores por períodos específicos, informe um ou mais períodos separados por vírgula (,)');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1385, 'pesquisa', 'number', '', 'Identificador da pesquisa ');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1386, 'posicao', 'string', '', 'Posição de apenas um indicador ou um ou mais identificadores delimitados pelo caractere | (pipe). Assume por padrão o valor 0, uma referência abstrata a qualquer indicador.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1387, 'scope', 'string', '', 'Usado com o parâmetro posição, restringe os indicadores. ');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1388, 'localidade', 'string', '', 'Uma ou mais localidades delimitadas por vírgula');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1389, 'periodos', 'string', '', 'Período do qual se deseja obter os indicadores.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1390, 'q', 'string', '', 'Critério de busca');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1391, 'indicador ', 'string', '', 'Um ou mais identificadores de indicador separados pelo caracter | (pipe)');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1392, 'orderBy', 'string', '', 'Informe como será definido o ordenamento. Pode assumir os valores ASC ou DESC. Por padrão, assume o valor DESC');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1393, 'contexto', 'string', '', 'Contextos do qual será obtido as localidades. Caso mais de um contexto seja informado, use vírgula para separá-los. Por padrão, assume o valor BR. Pode ainda representar uma região do país, como N, NE, SE, S ou CE ou ainda uma Unidade da Federação - Neste caso, usar o código do IBGE');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1394, 'natureza', 'number', '', 'Natureza das localidades obtidas do contexto. Pode ser 3 ou 4, indicando Unidade da Federação e municípios, respectivamente. Por padrão, assume o valor 4');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1395, 'offset', 'number ', '', 'Quando especificado, informa o deslocamento relativo ao primeiro registro - Similar à palavra-chave de mesmo nome usado por alguns sistemas de gerenciamento de banco de dados. Usado com os parâmetros lower e upper.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1396, 'lower', 'number', '', 'Quantidade de registros, a serem retornados, anteriores àquele especificado pelo parâmetro offset ou localidade');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1397, 'upper', 'number ', '', 'Quantidade de registros, a serem retornados, posteriores àquele especificado pelo parâmetro offset ou localidade');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1398, 'localidades', 'string', '', 'Equivalente ao parâmetro offset, exceto pelo fato de que o deslocamento é relativo a uma dada localidade');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1399, 'groupBy', 'string', '', 'Por padrão, os resultados são agrupados pelo identificador da indicador. Caso deseje que sejam agrupados pelo identificador da localidade, configure o parâmetro groupBy com o valor localidade');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1400, 'arquivo', 'file', '', 'Arquivo de observação RINEX com uma das seguintes extensões: .rnx, .crx, .obs, .05o a .24o, .05d a .24d, .zip, .tar, .tgz, .gz, .7z');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1401, 'tipo-levantamento', 'string', '', 'Modo de processamento dos dados. Aceita os valores estático ou cinemático.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1402, 'modelo-antena', 'string', '', 'Modelo da antena, conforme nomenclatura adotada pelo IGS ou NGS');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1403, 'altura-antena', 'number', '', 'Altura da antena em metros, que corresponde à medida vertical entre o ponto de referência do marco e o plano de referência da antena (ARP). Usar caractere de ponto como separador decimal (formato americano).');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1404, 'marco-ibge', 'string', '', 'Identificador do marco geodésico, caso pertença à Rede Geodésica Brasileira.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1405, 'email', 'string', '', 'E-mail para recebimento dos dados processados. Este é um campo obrigatório, mas outra forma de se obter a resposta, além do e-mail, é analisando o JSON de retorno e realizando o download dos resultados pela URL fornecida.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1406, 'autorizacao-uso', 'string', '', 'Autorização para que os dados fornecidos pelo usuário sejam usados pelo IBGE, visando a melhoria do serviço.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1407, 'arquivo', 'file', '', 'Arquivo para processamento, com as coordenadas de um ponto por linha. ');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1408, 'referencialEntrada', 'string', '', 'Sistema de referência e realização das coordenadas de entrada. O referencial de saída é sempre SIRGAS2000.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1409, 'tipoCoordenadaEntrada', 'string', '', 'Ordem dos termos no arquivo enviado. Por exemplo, "id_lat_long_alt_gd" significa que as coordenadas estão em graus decimais e que primeiro vem o ID, depois a Latitude, a Longitude e por último a Altitude.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1410, 'tipoCoordenadaSaida', 'string', '', 'Formatos aceitos para as coordenadas de saída:');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1411, 'tipoEntrada', 'string', '', 'Valores aceitos: "txt" (para arquivos texto) ou "csv" (para arquivos csv separados por ponto-e-vírgula).');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1412, 'tipoSaida', 'string', '', 'Valores aceitos: "json" (para processamento síncrono) ou "arquivo" (para processamento assíncrono - resposta por e-mail).');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1413, 'email', 'string', '', 'E-mail para recebimento dos resultados. É opcional se o arquivo tiver menos do que 10 KB de tamanho.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1414, 'lat', 'string', '', 'Graus da latitude variando de 0 a -90 no hemisfério sul e 0 a 90 no hemisfério norte.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1415, 'long', 'string', '', 'Graus da longitude variando de 0 a 180 a leste de Greenwich e 0 a -180 a oeste de Greenwich.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1416, 'alt ', 'string', '', 'Altitude em metros.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1417, 'pontos ', 'string', '', 'Array de pontos com todos no formato de grau decimal.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1418, 'referencialEntrada', 'string', '', 'Sistema de referência e realização das coordenadas de entrada. O referencial de saída é sempre SIRGAS2000.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1419, 'tipoCoordenadaSaida', 'string', '', 'Formatos aceitos para as coordenadas de saída:');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1420, 'e', 'string', '', 'Componente Leste (UTM).Valor no intervalo [160000, 834000].');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1421, 'n', 'string', '', 'Componente Norte (UTM).Valor no intervalo [0, 10000000].');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1422, 'fuso', 'string', '', 'Componente Fuso (UTM).Valor no intervalo [18, 25].');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1423, 'x', 'string', '', 'Componente X (XYZ).Valor no intervalo [1435400, 5293295].');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1424, 'y', 'string', '', 'Componente Y (XYZ).Valor no intervalo [-6110598, -2919972].');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1425, 'z', 'string', '', 'Componente Z (XYZ).Valor no intervalo [-3554106, 607243.6].');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1426, 'produto', 'string', '', 'Nome do produto a ser buscado.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1427, 'qtd', 'integer', '', 'Quantidade de itens por página.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1428, 'page', 'integer', '', 'Página desejada da listagem, levando em conta a quantidade de itens por página.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1429, 'estacao ', 'string', '', 'Sigla da estação da qual se deseja obter as informações, formada por quatro caracteres.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1430, 'ano', 'number', 'yyyy', 'Ano a partir do qual se deseja obter as informações, seguindo o formato yyyy, como 2022, por exemplo.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1431, 'dia', 'number', 'ddd', 'Dia corrido do ano, a partir do qual se deseja obter as informações. Segue o formato ddd, como 033, que representa o 33º dia do ano (02 de fevereiro).');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1432, 'hora', 'number', 'hh', 'Hora a partir do qual se deseja obter as informações. Segue o formato hh, podendo variar de 00 a 23.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1433, 'minuto', 'number', '', 'Minuto a partir do qual se deseja obter as informações. Segue o formato mm, podendo assumir apenas os valores 0, 15, 30 e 45.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1434, 'tipo', 'string', '', 'Tipo de dados, podendo aceitar os valores MO, para dados de observações, ou MN, para dados de navegação.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1435, 'maregrafo', 'string', '', 'Uma sigla de identificação do marégrafo.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1436, 'momentoInicial', 'string', 'aaaa-mm-dd-hh-mi', 'Utilize este filtro para estabelecer a data e hora de início dos dados desejados.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1437, 'momentoFinal', 'string', 'aaaa-mm-dd-hh-mi', 'Utilize este filtro para estabelecer a data e hora de término dos dados desejados.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1438, 'codigoSensor', 'string', '', 'Código de identificação do sensor de nível do mar para o qual se deseja os dados. Uma lista de códigos pode ser fornecida utilizando o caracter | (pipe) como separador. Os valores válidos podem ser encontrados em Sensores.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1439, 'incluirPrevisao', 'string', '', 'Flag (S=Sim e N=Não) indicando se o usuário deseja receber os dados de previsão de maré associados.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1440, 'sensores', 'string', '', 'Código de identificação do sensor. Uma lista de códigos pode ser fornecida utilizando o caracter | (pipe) como separador. Os valores válidos podem ser encontrados em Sensores.');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1441, 'id', 'string', '', 'id de um conjunto de dado');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1442, 'nomeConjuntoDados', 'stering', '', 'Nome do conjunto de dados');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1443, 'dadosAbertos', 'boolean', 'true/false', 'Dados abertos');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1444, 'isPrivado', 'string', '', 'Privado');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1445, 'idOrganizacao', 'string ', '', 'Identificador da organização');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1446, 'pagina', 'integer', '', 'Página');
INSERT INTO Parameters (Id, Name, Type, Format, Description) VALUES (1445, 'filtro', 'object', '', 'Filtro');
